/style.css.content:
body {
    margin: 0;
    padding: 20px;
    font-family: system-ui, sans-serif;
    background: linear-gradient(135deg, #0f172a 0%, #1e293b 100%);
    color: white;
}

header {
    text-align: center;
    padding: 40px 0;
}

h1 {
    font-size: 3rem;
    background: linear-gradient(to right, #22d3ee, #3b82f6);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

main {
    max-width: 800px;
    margin: 0 auto;
}
